    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Art et Décor | Mot de passe oublié{% endblock %}

{% block content %}
{% for message in app.flashes('notice') %}
    <div class=\"alert alert-info\" style='margin-top:150px;'>{{ message }}</div>
{% endfor %}
<form method=\"post\" style=\"margin-top:200px;\">
    <h1 class=\"h3 mb-3 font-weight-normal\">Réinitialiser mon mot de passe</h1>
    <label for=\"inputEmail\">Email</label>
    <input type=\"email\" name=\"email\" id=\"inputEmail\" class=\"form-control\" required autofocus placeholder=\"Votre email\">

    {#
        Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
        See https://symfony.com/doc/current/security/remember_me.html

        <div class=\"checkbox mb-3\">
            <label>
                <input type=\"checkbox\" name=\"_remember_me\"> Remember me
            </label>
        </div>
    #}

    <button class=\"btn btn-lg btn-primary\" type=\"submit\">
        Valider
    </button>
</form>
<p>Pas encore de compte ? Cliquez <a href=\"{{ path('app_login') }}\">ICI</a>pour vous connecter</p>
{% endblock %}
", "reset_password/reset_password.html.twig", "C:\\wamp64\\www\\artetdecor-master\\templates\\reset_password\\reset_password.html.twig");
